CREATE MATERIALIZED VIEW afarma.produtos_promocao
TABLESPACE pg_default
AS SELECT p.id,
    p.nome,
    p.ean,
    p.descricao,
    p.categoria_id,
    p.marca_id,
    p.photo_id,
    p.departamento_id,
    p.principioativo_id,
    p.grupo_id,
    p.contraindicacao,
    p.indicacao,
    p.precomedio,
    p.seqnum
   FROM ( SELECT produto.id,
            produto.nome,
            produto.ean,
            produto.descricao,
            produto.categoria_id,
            produto.marca_id,
            produto.photo_id,
            produto.departamento_id,
            produto.principioativo_id,
            produto.grupo_id,
            produto.contraindicacao,
            produto.indicacao,
            produto.precomedio,
            row_number() OVER (PARTITION BY ("substring"(translate(lower(produto.nome::text), 'áàâãäåaaaÁÂÃÄÅAAAÀéèêëeeeeeEEEÉEEÈìíîïìiiiÌÍÎÏÌIIIóôõöoooòÒÓÔÕÖOOOùúûüuuuuÙÚÛÜUUUUçÇñÑıİ'::text, 'aaaaaaaaaAAAAAAAAAeeeeeeeeeEEEEEEEiiiiiiiiIIIIIIIIooooooooOOOOOOOOuuuuuuuuUUUUUUUUcCnNyY'::text), 1, 1)) ORDER BY (random())) AS seqnum
           FROM afarma.produto) p
  WHERE p.seqnum <= 5
WITH DATA;

SELECT uuid_generate_v4(), pp.id, now(), (now() + INTERVAL '30 day') as "datafim", 
(select l.id from afarma.loja l where l.active=true order by random() limit 1)
from afarma.produtos_promocao pp;


